package vn.hcmute.ktqt.ui.auth;

import android.content.Intent;
import android.os.Bundle;
import android.widget.Button;
import android.widget.EditText;
import android.widget.TextView;
import android.widget.Toast;

import androidx.appcompat.app.AppCompatActivity;

import retrofit2.Call;
import retrofit2.Callback;
import retrofit2.Response;
import vn.hcmute.ktqt.MainActivity;
import vn.hcmute.ktqt.R;
import vn.hcmute.ktqt.data.SessionManager;
import vn.hcmute.ktqt.models.AuthResponse;
import vn.hcmute.ktqt.models.requests.LoginRequest;
import vn.hcmute.ktqt.network.ApiService;
import vn.hcmute.ktqt.network.RetrofitClient;

public class LoginActivity extends AppCompatActivity {

    private EditText etUsername, etPassword;
    private Button btnLogin;
    private TextView tvRegister;

    private ApiService api;
    private SessionManager session;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_login);

        etUsername = findViewById(R.id.etUsername);
        etPassword = findViewById(R.id.etPassword);
        btnLogin = findViewById(R.id.btnLogin);
        tvRegister = findViewById(R.id.tvRegister);

        api = RetrofitClient.getClient(this).create(ApiService.class);
        session = new SessionManager(this);

        btnLogin.setOnClickListener(v -> doLogin());

        tvRegister.setOnClickListener(v -> startActivity(new Intent(this, RegisterActivity.class)));
    }

    private void doLogin() {
        String u = etUsername.getText().toString().trim();
        String p = etPassword.getText().toString().trim();
        if (u.isEmpty() || p.isEmpty()) {
            Toast.makeText(this, "Nhập username và password", Toast.LENGTH_SHORT).show();
            return;
        }

        api.login(new LoginRequest(u, p)).enqueue(new Callback<AuthResponse>() {
            @Override
            public void onResponse(Call<AuthResponse> call, Response<AuthResponse> response) {
                if (response.isSuccessful() && response.body() != null) {
                    session.saveToken(response.body().accessToken);
                    startActivity(new Intent(LoginActivity.this, MainActivity.class));
                    finish();
                } else {
                    Toast.makeText(LoginActivity.this, "Đăng nhập thất bại", Toast.LENGTH_SHORT).show();
                }
            }

            @Override
            public void onFailure(Call<AuthResponse> call, Throwable t) {
                Toast.makeText(LoginActivity.this, "Lỗi mạng khi đăng nhập", Toast.LENGTH_SHORT).show();
            }
        });
    }
}

package vn.hcmute.ktqt.ui.auth;

import android.content.Intent;
import android.os.Bundle;
import android.widget.Button;
import android.widget.EditText;
import android.widget.Toast;

import androidx.appcompat.app.AppCompatActivity;

import retrofit2.Call;
import retrofit2.Callback;
import retrofit2.Response;
import vn.hcmute.ktqt.R;
import vn.hcmute.ktqt.models.requests.RegisterRequest;
import vn.hcmute.ktqt.network.ApiService;
import vn.hcmute.ktqt.network.RetrofitClient;

public class RegisterActivity extends AppCompatActivity {

    private EditText etUsername, etEmail, etPhone, etPassword;
    private Button btnRegister;
    private ApiService api;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_register);

        etUsername = findViewById(R.id.etUsername);
        etEmail = findViewById(R.id.etEmail);
        etPhone = findViewById(R.id.etPhone);
        etPassword = findViewById(R.id.etPassword);
        btnRegister = findViewById(R.id.btnRegister);

        api = RetrofitClient.getClient(this).create(ApiService.class);

        btnRegister.setOnClickListener(v -> doRegister());
    }

    private void doRegister() {
        String u = etUsername.getText().toString().trim();
        String email = etEmail.getText().toString().trim();
        String phone = etPhone.getText().toString().trim();
        String p = etPassword.getText().toString().trim();

        if (u.isEmpty() || p.isEmpty() || email.isEmpty() || phone.isEmpty()) {
            Toast.makeText(this, "Nhập đầy đủ thông tin", Toast.LENGTH_SHORT).show();
            return;
        }

        RegisterRequest req = new RegisterRequest(u, p, email, phone);
        api.register(req).enqueue(new Callback<Void>() {
            @Override
            public void onResponse(Call<Void> call, Response<Void> response) {
                if (response.isSuccessful()) {
                    // assume backend returns 200 and sends OTP to phone/email; navigate to OTP screen
                    Intent it = new Intent(RegisterActivity.this, RegisterOtpActivity.class);
                    // if backend returns a userId, pass it via intent extras; for now we'll pass username as identifier
                    it.putExtra("userIdentifier", u);
                    startActivity(it);
                    finish();
                } else {
                    Toast.makeText(RegisterActivity.this, "Đăng ký thất bại", Toast.LENGTH_SHORT).show();
                }
            }

            @Override
            public void onFailure(Call<Void> call, Throwable t) {
                Toast.makeText(RegisterActivity.this, "Lỗi mạng khi đăng ký", Toast.LENGTH_SHORT).show();
            }
        });
    }
}

package vn.hcmute.ktqt.ui.auth;

import android.content.Intent;
import android.os.Bundle;
import android.widget.Button;
import android.widget.EditText;
import android.widget.Toast;

import androidx.appcompat.app.AppCompatActivity;

import retrofit2.Call;
import retrofit2.Callback;
import retrofit2.Response;
import vn.hcmute.ktqt.MainActivity;
import vn.hcmute.ktqt.R;
import vn.hcmute.ktqt.data.SessionManager;
import vn.hcmute.ktqt.models.AuthResponse;
import vn.hcmute.ktqt.models.requests.OtpRequest;
import vn.hcmute.ktqt.network.ApiService;
import vn.hcmute.ktqt.network.RetrofitClient;

public class RegisterOtpActivity extends AppCompatActivity {

    private EditText etOtp;
    private Button btnVerify;
    private ApiService api;
    private SessionManager session;
    private String userIdentifier;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_register_otp);

        etOtp = findViewById(R.id.etOtp);
        btnVerify = findViewById(R.id.btnVerify);

        api = RetrofitClient.getClient(this).create(ApiService.class);
        session = new SessionManager(this);

        userIdentifier = getIntent().getStringExtra("userIdentifier");

        btnVerify.setOnClickListener(v -> doVerify());
    }

    private void doVerify() {
        String otp = etOtp.getText().toString().trim();
        if (otp.length() != 6) {
            Toast.makeText(this, "Nhập mã OTP 6 chữ số", Toast.LENGTH_SHORT).show();
            return;
        }

        OtpRequest req = new OtpRequest(userIdentifier, otp);
        api.verifyOtp(req).enqueue(new Callback<AuthResponse>() {
            @Override
            public void onResponse(Call<AuthResponse> call, Response<AuthResponse> response) {
                if (response.isSuccessful() && response.body() != null) {
                    session.saveToken(response.body().accessToken);
                    startActivity(new Intent(RegisterOtpActivity.this, MainActivity.class));
                    finish();
                } else {
                    Toast.makeText(RegisterOtpActivity.this, "Xác thực thất bại", Toast.LENGTH_SHORT).show();
                }
            }

            @Override
            public void onFailure(Call<AuthResponse> call, Throwable t) {
                Toast.makeText(RegisterOtpActivity.this, "Lỗi mạng khi xác thực OTP", Toast.LENGTH_SHORT).show();
            }
        });
    }
}

package vn.hcmute.ktqt.ui.intro;

import android.content.Intent;
import android.os.Bundle;
import android.widget.Button;

import androidx.appcompat.app.AppCompatActivity;

import vn.hcmute.ktqt.MainActivity;
import vn.hcmute.ktqt.R;
import vn.hcmute.ktqt.data.SessionManager;
import vn.hcmute.ktqt.ui.auth.LoginActivity;

public class IntroActivity extends AppCompatActivity {

    private Button btnStart;
    private SessionManager session;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_intro);

        btnStart = findViewById(R.id.btnStart);
        session = new SessionManager(this);

        btnStart.setOnClickListener(v -> {
            if (session.isLoggedIn()) {
                startActivity(new Intent(IntroActivity.this, MainActivity.class));
            } else {
                startActivity(new Intent(IntroActivity.this, LoginActivity.class));
            }
            finish();
        });
    }
}
